BEGIN TRANSACTION;
CREATE TABLE IF NOT EXISTS "admin" (
	"id"	INTEGER,
	"username"	TEXT NOT NULL UNIQUE,
	"password"	TEXT NOT NULL,
	PRIMARY KEY("id" AUTOINCREMENT)
);
CREATE TABLE IF NOT EXISTS "catatan_internal" (
	"id"	INTEGER,
	"laporan_id"	INTEGER NOT NULL,
	"admin_username"	TEXT NOT NULL,
	"catatan"	TEXT NOT NULL,
	"timestamp"	DATETIME DEFAULT CURRENT_TIMESTAMP,
	PRIMARY KEY("id" AUTOINCREMENT),
	FOREIGN KEY("laporan_id") REFERENCES "laporan"("id") ON DELETE CASCADE
);
CREATE TABLE IF NOT EXISTS "dinas" (
	"id"	INTEGER,
	"nama_dinas"	TEXT NOT NULL UNIQUE,
	"kontak"	TEXT,
	PRIMARY KEY("id" AUTOINCREMENT)
);
CREATE TABLE IF NOT EXISTS "ignored_duplicates" (
	"report_id_1"	INTEGER NOT NULL,
	"report_id_2"	INTEGER NOT NULL,
	PRIMARY KEY("report_id_1","report_id_2")
);
CREATE TABLE IF NOT EXISTS "kategori" (
	"id"	INTEGER,
	"nama_kategori"	TEXT NOT NULL UNIQUE,
	"deskripsi"	TEXT,
	PRIMARY KEY("id" AUTOINCREMENT)
);
CREATE TABLE IF NOT EXISTS "laporan" (
	"id"	INTEGER,
	"nomor_laporan"	TEXT NOT NULL UNIQUE,
	"nama_pelapor"	TEXT NOT NULL,
	"no_whatsapp"	TEXT,
	"judul"	TEXT NOT NULL,
	"deskripsi"	TEXT NOT NULL,
	"kategori_id"	INTEGER NOT NULL,
	"kecamatan"	TEXT,
	"kelurahan"	TEXT,
	"foto"	TEXT,
	"foto_selesai"	TEXT,
	"dinas_penugas"	TEXT,
	"pengumuman_publik"	TEXT,
	"latitude"	REAL NOT NULL,
	"longitude"	REAL NOT NULL,
	"status"	TEXT NOT NULL DEFAULT 'Diterima',
	"support_count"	INTEGER DEFAULT 0,
	"timestamp"	DATETIME DEFAULT CURRENT_TIMESTAMP,
	PRIMARY KEY("id" AUTOINCREMENT),
	FOREIGN KEY("kategori_id") REFERENCES "kategori"("id")
);
CREATE TABLE IF NOT EXISTS "nomor_laporan_harian" (
	"tanggal"	TEXT,
	"urutan_terakhir"	INTEGER NOT NULL,
	PRIMARY KEY("tanggal")
);
CREATE TABLE IF NOT EXISTS "riwayat_aksi" (
	"id"	INTEGER,
	"laporan_id"	INTEGER NOT NULL,
	"admin_username"	TEXT NOT NULL,
	"aksi"	TEXT NOT NULL,
	"timestamp"	DATETIME DEFAULT CURRENT_TIMESTAMP,
	PRIMARY KEY("id" AUTOINCREMENT),
	FOREIGN KEY("laporan_id") REFERENCES "laporan"("id") ON DELETE CASCADE
);
COMMIT;
